import React from 'react';
import styles from './AudioControls.module.css';
import pause from '../../../assets/images/custom-audio-player/pause.png';
import play from '../../../assets/images/custom-audio-player/play.png';
import trash from '../../../assets/images/file-picker-area/trash.png';

export default function AudioControls({ isPaused, pauseRecording, resumeRecording, stopRecording }) {
    return (
        <div className={styles.controls}>
            <button type='button' onClick={isPaused ? resumeRecording : pauseRecording}>
                {isPaused ? (
                    <img src={play} alt='play' />
                ) : (
                    <img src={pause} alt='pause' />
                )}

            </button>
            <button type='button' onClick={stopRecording}>
                <img src={trash} alt='remove'/>
            </button>
        </div>
    );
}
