import React from 'react';
import styles from '../styles/CustomScrollbar.module.css';

const CustomScrollbar = React.forwardRef(({ style, ...props }, ref) => {
  return (
    <div
      ref={ref}
      style={{
        ...style,
        width: 8,
        borderRadius: 4,
        backgroundColor: '#bbb',
        position: 'absolute',
        right: 10, // отодвинуть от правого края
        cursor: 'pointer',
        transition: 'background-color 0.3s',
      }}
      className="custom-scrollbar-thumb"
      {...props}
    />
  );
});

export default CustomScrollbar;
