import { useRef, useMemo, memo } from 'react';
import styles from '../styles/EmojiPicker.module.css';
import emojiData from 'unicode-emoji-json/data-by-group';
import DataSeparator from './DataSeparator';

const EmojiPicker = (props) => {
    const { onSelect, mainEmojis } = props;
    const groupRefs = useRef({});

    const emojis = useMemo(() => Object.entries(emojiData), []);

    const handleScrollToGroup = (groupKey) => {
        const ref = groupRefs.current[groupKey];
        if (ref) {
            ref.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
    };

    const renderNavigation = () => (
        <div className={styles.nav}>
            {/* Кнопка для часто используемых */}
            {mainEmojis?.length > 0 && (
                <button
                    className={styles.emoji}
                    onClick={() => handleScrollToGroup('frequent')}
                    type="button"
                    title="Häufig benutzt"
                >
                    ⭐
                </button>
            )}
            {emojis.map(([groupKey, data]) => (
                <button
                    key={groupKey}
                    className={styles.emoji}
                    onClick={() => handleScrollToGroup(groupKey)}
                    type="button"
                    title={data.name}
                >
                    {data.emojis[0]?.emoji || '❓'}
                </button>
            ))}
        </div>
    );

    const renderEmojis = () => (
        <>
            {/* Часто используемые */}
            {mainEmojis?.length > 0 && (
                <div
                    ref={(el) => (groupRefs.current['frequent'] = el)}
                    className={styles.groupContainer}
                >
                    <DataSeparator
                        styleContainer={{ marginTop: '14px' }}
                        styleText={{
                            fontSize: '14px',
                            fontWeight: '400',
                            fontFamily: '"Raleway", sans-serif',
                            color: '#ADADBD',
                            margin: '0 15px',
                            letterSpacing: '0%',
                        }}
                        data="Häufig benutzt"
                    />
                    {mainEmojis.map((emoji) => (
                        <button
                            key={emoji}
                            onClick={() => onSelect(emoji)}
                            className={styles.emoji}
                            type="button"
                            aria-label={emoji}
                            title={emoji}
                        >
                            {emoji}
                        </button>
                    ))}
                </div>
            )}
            {/* Все группы */}
            {emojis.map(([groupKey, data]) => (
                <div
                    key={groupKey}
                    ref={(el) => (groupRefs.current[groupKey] = el)}
                    className={styles.groupContainer}
                >
                    <DataSeparator
                        styleContainer={{ marginTop: '14px' }}
                        styleText={{
                            fontSize: '14px',
                            fontWeight: '400',
                            fontFamily: '"Raleway", sans-serif',
                            color: '#ADADBD',
                            margin: '0 15px',
                            letterSpacing: '0%',
                        }}
                        data={data.name}
                    />
                    {data.emojis.map((emojiData) => (
                        <button
                            key={emojiData.emoji}
                            onClick={() => onSelect(emojiData.emoji)}
                            className={styles.emoji}
                            type="button"
                            aria-label={emojiData.name}
                            title={emojiData.name}
                        >
                            {emojiData.emoji}
                        </button>
                    ))}
                </div>
            ))}
        </>
    );

    return (
        <div className={styles.container}>
            {renderNavigation()}
            <div className={styles.emojiSection}>
                {renderEmojis()}
            </div>
        </div>
    );
};

export default memo(EmojiPicker);
