import React, { memo, useEffect, useCallback, useState } from "react";
import styles from './MediaForPreview.module.css';
import LargeImageModal from '../large-image-modal/LargeImageModal';
import { sortImageUrlsByAspectRatio } from '../../../utils/imageUtils';
import isEqual from 'lodash.isequal';

function MediaForPreview({ media, onRemove, onBlurToggle }) {
    const [sortedImageUrls, setSortedImageUrls] = useState([]);
    const [selectedImage, setSelectedImage] = useState(null);
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleSelectImage = useCallback((src) => {
        const index = sortedImageUrls.findIndex(img => img.src === src);
        if (index >= 0) {
            setCurrentIndex(index);
            setSelectedImage(src);
        }
    }, [sortedImageUrls]);

    useEffect(() => {
        let isMounted = true;

        async function sortImages() {
            if (!Array.isArray(media) || media.length === 0) {
                if (isMounted) setSortedImageUrls([]);
                return;
            }

            const sorted = await sortImageUrlsByAspectRatio(media);
            if (!isMounted) return;
            setSortedImageUrls(sorted);
        }

        sortImages();

        return () => {
            isMounted = false;
        };
    }, [media]);

    const imgOrVideo = useCallback((src, isBlurred, onClick) => {
        const isVideo = /\.(mp4|webm|ogg)$/i.test(src);
        const mediaClass = `${styles.medium} ${isBlurred ? styles.blurred : ''}`;

        return (
            <div className={styles.mediaWrapper}>
                {isVideo ? (
                    <video controls className={mediaClass} src={src} onClick={onClick} />
                ) : (
                    <img src={src} alt="media" className={mediaClass} onClick={onClick} style={{ cursor: 'pointer' }} />
                )}
                <div className={styles.mediaButtons}>
                    <button onClick={() => onBlurToggle(src)}>
                        <img src="" alt="blur" />
                    </button>
                    <button onClick={() => onRemove(src)}>
                        <img src="" alt="delete" />
                    </button>
                </div>
            </div>
        );
    }, [onRemove, onBlurToggle]);

    const renderImages = useCallback((media) => {
        const handleClick = (src) => handleSelectImage(src);
        const wrap = (m) => imgOrVideo(m.src, m.isBloored, () => handleClick(m.src));

        switch (media.length) {
            case 1:
                return (
                    <div className={media[0].ratio > 1 ? styles.medium1hor : styles.medium1ver}>
                        {wrap(media[0])}
                    </div>
                );
            case 2:
                return (
                    <div className={styles.media2}>
                        <div>{wrap(media[0])}</div>
                        <div>{wrap(media[1])}</div>
                    </div>
                );
            case 3:
                return (
                    <div className={styles.media3}>
                        <div className={styles.media3Item}>{wrap(media[0])}</div>
                        <div className={`${styles.media3Column} ${styles.media3Item}`}>
                            <div className={styles.media3ColumnItem}>{wrap(media[1])}</div>
                            <div className={styles.media3ColumnItem}>{wrap(media[2])}</div>
                        </div>
                    </div>
                );
            case 4:
                return (
                    <div className={styles.media4}>
                        {[0, 2].map(col => (
                            <div key={col} className={styles.media4Column}>
                                {[0, 1].map(row => (
                                    <div key={row} className={styles.media4ColumnItem}>
                                        {wrap(media[col + row])}
                                    </div>
                                ))}
                            </div>
                        ))}
                    </div>
                );
            case 5:
                return (
                    <div className={styles.media5}>
                        <div className={styles.media5Item}>{wrap(media[0])}</div>
                        <div className={styles.media5Column}>
                            <div className={styles.media5ColumnItem}>{wrap(media[1])}</div>
                            <div className={styles.media5ColumnItem}>{wrap(media[2])}</div>
                        </div>
                        <div className={styles.media5Column}>
                            <div className={styles.media5ColumnItem}>{wrap(media[3])}</div>
                            <div className={styles.media5ColumnItem}>{wrap(media[4])}</div>
                        </div>
                    </div>
                );
            default:
                return null;
        }
    }, [imgOrVideo, handleSelectImage]);

    return (
        <>
            <div className={styles.media}>
                {renderImages(sortedImageUrls)}
            </div>
            {selectedImage && (
                <LargeImageModal
                    src={selectedImage}
                    onClose={() => setSelectedImage(null)}
                    images={sortedImageUrls}
                    currentIndex={currentIndex}
                    setCurrentIndex={(index) => {
                        setCurrentIndex(index);
                        const img = sortedImageUrls[index];
                        setSelectedImage(img?.src ?? null);
                    }}
                />
            )}
        </>
    );
}

export default memo(MediaForPreview);
