import React, { useState, useMemo } from 'react';
import styles from './FilePreviewArea.module.css';
import MediaForPreview from '../media/MediaForPreview';

function FilePreviewArea({ filesControl, onClose }) {
    const { files, setFiles } = filesControl;
    const [blurAll, setBlurAll] = useState(false);

    const media = useMemo(() =>
        files.map(file => ({
            file,
            src: URL.createObjectURL(file),
            name: file.name,
            type: file.type,
            ratio: 1,
            isBloored: file.isBloored ?? false,
        })), [files]);

    const imageMedia = useMemo(
        () => media.filter(item => item.type.startsWith('image') || item.type.startsWith('video')),
        [media]
    );

    const otherFiles = useMemo(
        () => media.filter(item => !item.type.startsWith('image') && !item.type.startsWith('video')),
        [media]
    );

    const handleRemove = (srcToRemove) => {
        setFiles(prev => prev.filter(f => URL.createObjectURL(f) !== srcToRemove));
    };

    const handleToggleBlur = (srcToToggle) => {
        setFiles(prev =>
            prev.map(f => {
                const fileUrl = URL.createObjectURL(f);
                if (fileUrl === srcToToggle) {
                    return {
                        ...f,
                        isBloored: !f.isBloored,
                    };
                }
                return f;
            })
        );
    };

    const toggleBlurAll = () => {
        setBlurAll(prev => !prev);
        setFiles(prev =>
            prev.map(f => {
                if (f.type.startsWith('image') || f.type.startsWith('video')) {
                    return {
                        ...f,
                        isBloored: !blurAll,
                    };
                }
                return f;
            })
        );
    };

    return (
        <div className={styles.overlay}>
            <div className={styles.header}>
                <button className={styles.button} onClick={toggleBlurAll}>
                    {blurAll ? 'Remove Blur' : 'Blur All Images'}
                </button>
                <button className={styles.closeButton} onClick={onClose}>âœ•</button>
            </div>

            <div className={styles.content}>
                {imageMedia.length > 0 && (
                    <div className={styles.mediaBlock}>
                        <MediaForPreview
                            media={imageMedia}
                            onRemove={handleRemove}
                            onBlurToggle={handleToggleBlur}
                        />
                    </div>
                )}

                {otherFiles.length > 0 && (
                    <div className={styles.filesBlock}>
                        <h3>Other files</h3>
                        <ul>
                            {otherFiles.map(f => (
                                <li key={f.src}>
                                    {f.name}
                                    <button onClick={() => handleRemove(f.src)}>Remove</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default FilePreviewArea;
