import React from "react";
import MessageInput from '../../../shared/message-input/MessageInput';
import { useModal } from "../../../../contexts/ModalContext";
import styles from '../styles/MessageInputContainer.module.css';

function MessageInputContainer({ messageControl, mediaControl, gifControl, audioRecordingControl }) {
    const { isModalOpen } = useModal();
    alert(isModalOpen);
    return (
        <div className={`${styles.messageInputContainer} ${isModalOpen ? styles.hidden : ''}`}>
            <MessageInput
                messageControl={messageControl}
                mediaControl={mediaControl}
                gifControl={gifControl}
                audioRecordingControl={audioRecordingControl} />
        </div>
    )
}

export default MessageInputContainer;
