import React from "react";
import styles from './MessageInput.module.css';
import smiley from '../../../../assets/images/message-form/smiley.png';
import gifImg from '../../../../assets/images/message-form/gif.png';
import arrow from '../../../../assets/images/message-form/arrow.png';
import microfon from '../../../../assets/images/message-form/microfon.png';
import smileySelected from '../../../../assets/images/message-form/smiley-selected.png';
import gifImgSelected from '../../../../assets/images/message-form/gif-selected.png';
import arrowSelected from '../../../../assets/images/message-form/arrow-selected.png';
import microfonSelected from '../../../../assets/images/message-form/microfon-selected.png';
import cross from '../../../../assets/images/message-form/cross.png';
import GifPicker from '../../../shared/gif-picker/GifPicker';
import EmojiPicker from '../../../shared/emoji-picker/EmojiPicker';

function MessageInput({ setMessage, }) {

    const handleInput = () => {
        const textarea = textareaRef.current;
        if (textarea) {
            textarea.style.height = 'auto';
            textarea.style.height = textarea.scrollHeight + 'px';
        }
    };

    return (<form name="messageInput" className={styles.messageForm}>
        <textarea
            ref={textareaRef}
            name="message"
            className={styles.messageInput}
            autoComplete="off"
            placeholder="Напишіть ваше повідомлення"
            onInput={handleInput}
            rows={1}
            onChange={(e) => setMessage(e.target.value)}
        />
        <div className={styles.messageImages}>
            <button type="button" onClick={handleEmojiClick}>
                {isEmojiPickerOpen ?
                    (<img src={smileySelected} alt="emoji" />) : (<img src={smiley} alt="emoji" />)
                }
            </button>
            {isEmojiPickerOpen && (
                <div className={styles.emojiWrapper} ref={emojiPickerRef}>
                    <div className={styles.emojiContainer}>
                        <EmojiPicker onSelect={handleEmojiSelect} mainEmojis={mainEmojis} />
                    </div>
                </div>
            )}
            <button type="button" onClick={handleGifClick}>
                {isGifPanelOpen ? (
                    <img src={gifImgSelected} alt="gif" />
                ) : <img src={gifImg} alt="gif" />}
            </button>

            {isGifPanelOpen && (
                <div className={styles.emojiWrapper} ref={gifPanelRef}>
                    <div className={styles.emojiContainer}>
                        <GifPicker onSelect={handleGifSelect} setIsGifPanelOpen={setIsGifPanelOpen} />
                    </div>
                </div>
            )}

            <button type="button" onClick={() => fileInputRef.current?.click()}>
                <img src={cross} alt="attach media" />
            </button>
            <input
                type="file"
                accept="image/*,video/*"
                multiple
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={handleImageUpload}
            />
            <button type="button" onClick={handleAudioClick}>
                {isRecording ? (<img src={microfonSelected} alt="audio" />) : (<img src={microfon} alt="audio" />)}
            </button>

            <button type="submit"><img src={arrow} alt="send" /></button>
        </div>
    </form>
    )
}

export default MessageInput;
