import React, { useState, useEffect, useCallback } from 'react';
import styles from '../styles/GifPicker.module.css';
import cross from '../assets/images/gif-picker/cross.png';
import magnifier from '../assets/images/gif-picker/magnifier.png';
import selected from '../assets/images/gif-picker/selected.png';
import watch from '../assets/images/gif-picker/watch.png';

const TENOR_API_KEY = 'AIzaSyBHdF6XBz7mujjoH1Sj25AiE7sdvZrJonY';
const TENOR_ENDPOINT = 'https://tenor.googleapis.com/v2/search';

function GifPicker({ onSelect, setIsGifPanelOpen }) {
    const [query, setQuery] = useState('');
    const [gifs, setGifs] = useState([]);

    const handleCloseGifPanel = () => {
        setIsGifPanelOpen(false);
    }

    const fetchGifs = useCallback(async () => {
        try {
            const searchQuery = query.trim() || "smile";
            const url = `${TENOR_ENDPOINT}?q=${encodeURIComponent(searchQuery)}&key=${TENOR_API_KEY}&limit=48`;
            const response = await fetch(url);
            const data = await response.json();
            setGifs(data.results || []);
        } catch (error) {
            console.error('Ошибка при загрузке GIF:', error);
        }
    }, [query]);

    return (
        <div className={styles.container}>
            <div className={styles.header}>
                <div className={styles.headerImages}>
                    <img src={selected} alt='selected' />
                    <img src={watch} alt='recent' />
                </div>
                <div className={styles.inputContainer}>
                    <input
                        type="text"
                        placeholder="Пошук"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        className={styles.searchInput}
                    />
                    <div className={styles.inputImages}>
                        <img
                            src={magnifier}
                            alt="search"
                            className={styles.clickable}
                            onClick={fetchGifs}
                        />
                        <img
                            src={cross}
                            alt="close"
                            onClick={() => handleCloseGifPanel()}
                            className={styles.clickable}
                        />
                    </div>
                </div>
            </div>
            <div className={styles.gifGrid}>
                {gifs.map((gif) => (
                    <button
                        key={gif.id}
                        onClick={() => onSelect(gif.media_formats.gif.url)}
                        className={styles.gifButton}
                    >
                        <img
                            src={gif.media_formats.tinygif.url}
                            alt={gif.content_description}
                            className={styles.gifImage}
                        />
                    </button>
                ))}
            </div>
        </div>
    );
}

export default GifPicker;
