import React, { useRef, useState, useEffect } from 'react';
import styles from './CustomAudioPlayer.module.css';
import pause from '../../../assets/images/custom-audio-player/pause.png';
import play from '../../../assets/images/custom-audio-player/play.png';
import AudioTimer from '../audio-recorder/AudioTimer';

function CustomAudioPlayer({ src }) {
  const audioRef = useRef(null);

  const [isPlaying, setIsPlaying] = useState(false);
  const [duration, setDuration]   = useState(0);
  const [currentTime, setCurrentTime] = useState(0);

  const onLoadedMetadata = () => {
    const audio = audioRef.current;
    if (audio && !isNaN(audio.duration)) {
      setDuration(audio.duration);
    }
  };

  const onTimeUpdate = () => {
    const audio = audioRef.current;
    if (!audio) return;
    const ct = isNaN(audio.currentTime) ? 0 : audio.currentTime;
    setCurrentTime(ct);
  };

  const togglePlay = () => {
    const audio = audioRef.current;
    if (!audio) return;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  const onProgressClick = e => {
    const audio = audioRef.current;
    if (!audio || duration <= 0) return;

    const { left, width } = e.currentTarget.getBoundingClientRect();
    const clickX = e.clientX - left;
    const ratio = clickX / width;
    audio.currentTime = ratio * audio.duration;
    setCurrentTime(audio.currentTime);
  };

  useEffect(() => {
    setIsPlaying(false);
    setDuration(0);
    setCurrentTime(0);
  }, [src]);

  const progressPercent = audioRef ?? audioRef.current.duration > 0
    ? (currentTime / audioRef.current.duration) * 100
    : 0;

  return (
    <div className={styles.audioPlayer}>
      <audio
        ref={audioRef}
        src={src}
        preload="metadata"
        onLoadedMetadata={onLoadedMetadata}
        onTimeUpdate={onTimeUpdate}
        onEnded={() => setIsPlaying(false)}
      />

      <button
        type="button"
        onClick={togglePlay}
        className={styles.playButton}
        aria-label={isPlaying ? 'Pause' : 'Play'}
      >
        {isPlaying
          ? <img src={pause} alt="Pause" className={styles.play} />
          : <img src={play} alt="Play" />}
      </button>

      <div
        className={styles.progressBar}
        role="progressbar"
        aria-valuemin={0}
        aria-valuemax={duration}
        aria-valuenow={currentTime}
        tabIndex={0}
        onClick={onProgressClick}
      >
        <div
          className={styles.progress}
          style={{ width: `${progressPercent}%` }}
        />
      </div>

      <AudioTimer
        isPaused={!isPlaying}
        time={currentTime}
        withDot={false}
      />
    </div>
  );
}

export default CustomAudioPlayer;
