// AudioPlayer.jsx
import React, { useRef, useState, useEffect, useCallback } from 'react';

const AudioPlayer = ({ src, barCount = 80, barWidth = 2, barGap = 2, 
                       playedColor = '#1AAAF5', unplayedColor = '#BBBBBE' }) => {
  const audioRef = useRef(null);
  const canvasRef = useRef(null);
  const [peaks, setPeaks] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentPeakIndex, setCurrentPeakIndex] = useState(0);

  // Загружаем и анализируем аудио, чтобы получить массив амплитуд
  useEffect(() => {
    if (!src) return;
    const fetchAndDecode = async () => {
      const resp = await fetch(src);
      const arrayBuffer = await resp.arrayBuffer();
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      const buffer = await audioCtx.decodeAudioData(arrayBuffer);
      const rawData = buffer.getChannelData(0); // первый канал
      const blockSize = Math.floor(rawData.length / barCount);
      const filtered = new Float32Array(barCount);
      for (let i = 0; i < barCount; i++) {
        let sum = 0;
        for (let j = 0; j < blockSize; j++) {
          sum += Math.abs(rawData[i * blockSize + j]);
        }
        filtered[i] = sum / blockSize;
      }
      setPeaks(filtered);
    };
    fetchAndDecode();
  }, [src, barCount]);

  // Обновляем индекс текущей полоски при воспроизведении
  const onTimeUpdate = useCallback(() => {
    const audio = audioRef.current;
    if (!audio || peaks.length === 0) return;
    const idx = Math.floor((audio.currentTime / audio.duration) * peaks.length);
    setCurrentPeakIndex(idx);
  }, [peaks.length]);

  // Play / Pause
  const togglePlay = () => {
    const audio = audioRef.current;
    if (!audio) return;
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  // Рисуем полоски в канвасе
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const height = canvas.height;
    const totalBars = peaks.length;
    ctx.clearRect(0, 0, canvas.width, height);

    peaks.forEach((val, i) => {
      const barHeight = val * height;
      const x = i * (barWidth + barGap);
      ctx.fillStyle = i <= currentPeakIndex ? playedColor : unplayedColor;
      ctx.fillRect(x, (height - barHeight) / 2, barWidth, barHeight);
    });
  }, [peaks, currentPeakIndex, barWidth, barGap, playedColor, unplayedColor]);

  return (
    <div style={{ display: 'inline-block', userSelect: 'none' }}>
      <canvas
        ref={canvasRef}
        width={(barWidth + barGap) * barCount}
        height={150}
        style={{ verticalAlign: 'middle', cursor: 'pointer' }}
        onClick={togglePlay}
      />
      <audio
        ref={audioRef}
        src={src}
        onTimeUpdate={onTimeUpdate}
        onEnded={() => setIsPlaying(false)}
        style={{ display: 'none' }}
      />
      <button
        onClick={togglePlay}
        style={{
          marginLeft: 10,
          padding: '6px 12px',
          fontSize: 14,
          cursor: 'pointer',
          borderRadius: 4,
          border: '1px solid #888',
          background: isPlaying ? '#f44336' : '#2196F3',
          color: '#fff',
        }}
      >
        {isPlaying ? 'Pause' : 'Play'}
      </button>
    </div>
  );
};

export default AudioPlayer;
