import React, { useState, useEffect } from 'react';
import styles from '../styles/GifPicker.module.css';

const TENOR_API_KEY = 'ВАШ_API_КЛЮЧ';
const TENOR_ENDPOINT = 'https://tenor.googleapis.com/v2/search';

function GifPicker({ onSelect }) {
  const [query, setQuery] = useState('');
  const [gifs, setGifs] = useState([]);

  useEffect(() => {
    const fetchGifs = async () => {
      if (!query) return;
      try {
        const response = await fetch(
          `${TENOR_ENDPOINT}?q=${encodeURIComponent(query)}&key=${TENOR_API_KEY}&limit=20`
        );
        const data = await response.json();
        setGifs(data.results || []);
      } catch (error) {
        console.error('Ошибка при загрузке GIF:', error);
      }
    };

    const delayDebounce = setTimeout(() => {
      fetchGifs();
    }, 500);

    return () => clearTimeout(delayDebounce);
  }, [query]);

  return (
    <div className={styles.container}>
      <input
        type="text"
        placeholder="Поиск GIF"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className={styles.searchInput}
      />
      <div className={styles.gifGrid}>
        {gifs.map((gif) => (
          <button
            key={gif.id}
            onClick={() => onSelect(gif.media_formats.gif.url)}
            className={styles.gifButton}
          >
            <img
              src={gif.media_formats.tinygif.url}
              alt={gif.content_description}
              className={styles.gifImage}
            />
          </button>
        ))}
      </div>
    </div>
  );
}

export default GifPicker;
