import React, { useMemo } from 'react';
import styles from '../styles/Message.module.css'

function formatRelativeDateTime(date) {
    const now = new Date();

    const pad = (n) => n.toString().padStart(2, '0');
    const time = `${pad(date.getHours())}:${pad(date.getMinutes())}`;

    const isSameDay = (d1, d2) =>
        d1.getDate() === d2.getDate() &&
        d1.getMonth() === d2.getMonth() &&
        d1.getFullYear() === d2.getFullYear();

    const yesterday = new Date();
    yesterday.setDate(now.getDate() - 1);

    if (isSameDay(date, now)) {
        return `сьогодні ${time}`;
    } else if (isSameDay(date, yesterday)) {
        return `вчора ${time}`;
    } else {
        return `${pad(date.getDate())}.${pad(date.getMonth() + 1)}.${date.getFullYear()} ${time}`;
    }
}

const Message = React.memo(function Message(props) {
    const { content, sentAt, isEdited, isDeleted, isRead, images, user, isFirst } = props;
    const photosElements = React.useMemo(() => {
        return images.map((url, index) => (
            <img key={index} src={url} alt={`photo-${index}`} className={styles.contentPhoto} />
        ));
    }, [images]);


    const infoBlock = isFirst ? (
        <div className={`${styles.info} ${styles.infoFirst}`}>
            <div className={styles.infoImage}>
                <img src={user?.avatar || '/default-avatar.png'} alt="Avatar" />
            </div>
            <div className={styles.infoName}>
                {user.firstName} {user.lastName}
            </div>
            <div className={styles.infoTime}>
                {formatRelativeDateTime(sentAt)}
            </div>
            <div className={styles.isRead}>
                {isRead ? <p>Read</p> : <p>Not read</p>}
            </div>
        </div>
    ) : (
        <div className={styles.info}>
            <div className={styles.infoTime}>
                {formatRelativeDateTime(sentAt)}
            </div>
            <div className={styles.isRead}>
                {isRead ? <p>Read</p> : <p>Not read</p>}
            </div>
        </div>
    );


    const message = (
        <div className={styles.main}>
            {infoBlock}
            <div className={styles.content}>
                <div className={styles.contentText}>
                    {content}
                </div>
                {images && images.length > 0 && (
                    <div className={`${styles.contentPhotos} ${styles[`contentPhotos${images.length}`]}`}>
                        {photosElements}
                    </div>
                )}
            </div>
            <div className={styles.isEdited}>
                {isEdited ?? <p>Edited</p>}
            </div>
        </div>
    );
    return message;
});

export default Message;
