import { useRef, useMemo, memo } from 'react';
import { GroupedVirtuoso } from 'react-virtuoso';
import styles from './EmojiPicker.module.css';
import emojiData from 'unicode-emoji-json/data-by-group';
import DataSeparator from '../data-separator/DataSeparator';

import animal from '../../../assets/images/emojis/animal.png';
import ball from '../../../assets/images/emojis/ball.png';
import car from '../../../assets/images/emojis/car.png';
import diff from '../../../assets/images/emojis/diff.png';
import flag from '../../../assets/images/emojis/flag.png';
import food from '../../../assets/images/emojis/food.png';
import person from '../../../assets/images/emojis/person.png';
import smiley from '../../../assets/images/emojis/smiley.png';
import watch from '../../../assets/images/emojis/watch.png';
import bulb from '../../../assets/images/emojis/bulb.png';

const EmojiPicker = (props) => {
    const { onSelect, mainEmojis } = props;
    const virtuosoRef = useRef(null);

    const emojis = useMemo(() => Object.entries(emojiData), []);

    const whatImage = (groupName) => {
        if (groupName === 'Smileys & Emotion') return smiley;
        if (groupName === 'People & Body') return person;
        if (groupName === 'Animals & Nature') return animal;
        if (groupName === 'Food & Drink') return food;
        if (groupName === 'Travel & Places') return car;
        if (groupName === 'Activities') return ball;
        if (groupName === 'Objects') return bulb;
        if (groupName === 'Symbols') return diff;
        if (groupName === 'Flags') return flag;
        return null;
    };

    // Группируем данные для GroupedVirtuoso
    const groupedEmojis = useMemo(() => {
        const groups = [];

        if (mainEmojis?.length > 0) {
            groups.push({
                group: 'Часто використовувані',
                emojis: mainEmojis.map((emoji) => ({ emoji, name: emoji })),
                key: 'frequent',
            });
        }

        emojis.forEach(([groupKey, data]) => {
            groups.push({
                group: data.name,
                emojis: data.emojis,
                key: groupKey,
            });
        });

        return groups;
    }, [mainEmojis, emojis]);

    const groupLabels = groupedEmojis.map(g => g.group);
    const groupCounts = groupedEmojis.map(g => g.emojis.length);

    // Функция скролла к группе по индексу
    const handleScrollToGroup = (groupIndex) => {
        let itemIndex = 0;
        for (let i = 0; i < groupIndex; i++) {
            itemIndex += groupCounts[i];
        }
        virtuosoRef.current?.scrollToIndex({ index: itemIndex, align: 'start' });
    };

    const separatorStyles = {
        styleContainer: {
            width: '100%',
            color: '#2C2F36',
            marginTop: '8px'
        },
        styleText: {
            fontSize: '14px',
            fontWeight: '400',
            fontFamily: '"Raleway", sans-serif',
            color: '#ADADBD',
            margin: '0 15px',
            letterSpacing: '0%'
        },
        lineColor: '#ADADBD'
    };

    const renderNavigation = () => (
        <div className={styles.nav}>
            {mainEmojis?.length > 0 && (
                <button
                    className={styles.emoji}
                    onClick={() => handleScrollToGroup(0)}
                    type="button"
                    title="Часто використовувані"
                >
                    <div>
                        <img src={watch} alt="watch" />
                    </div>
                </button>
            )}
            {groupLabels.slice(mainEmojis?.length > 0 ? 1 : 0).map((groupName, idx) => (
                <button
                    key={groupName}
                    className={styles.emoji}
                    onClick={() => handleScrollToGroup(mainEmojis?.length > 0 ? idx + 1 : idx)}
                    type="button"
                    title={groupName}
                >
                    <div>
                        <img src={whatImage(groupName)} alt={groupName} />
                    </div>
                </button>
            ))}
        </div>
    );

    return (
        <div className={styles.container}>
            {renderNavigation()}
            <div className={styles.emojiSection}>
                <GroupedVirtuoso
                    ref={virtuosoRef}
                    groupCounts={groupCounts}
                    groupContent={(index) => (
                        <DataSeparator
                            separatorStyles={separatorStyles}
                            data={groupLabels[index]}
                        />
                    )}
                    itemContent={(index, emojiData) => (
                        <button
                            key={emojiData.emoji || emojiData}
                            onClick={() => onSelect(emojiData.emoji || emojiData)}
                            className={styles.emoji}
                            type="button"
                            aria-label={emojiData.name || emojiData}
                            title={emojiData.name || emojiData}
                        >
                            <div><span>{emojiData.emoji || emojiData}</span></div>
                        </button>
                    )}
                    style={{ height: 300, width: '100%' }}
                />
            </div>
        </div>
    );
};

export default memo(EmojiPicker);
