import React, { useRef, useState } from 'react';
import styles from './FilePicker.module.css';
import image from '../../../assets/images/file-picker/image.png';

function FilePicker({ onFileSelect }) {
    const fileInputRef = useRef(null);
    const [dragActive, setDragActive] = useState(false);

    const handleFileChange = (e) => {
        const files = e.target.files;
        if (files.length && onFileSelect) {
            onFileSelect(files[0]);
        }
    };

    const handleDragEnter = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(true);
    };

    const handleDragLeave = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        e.stopPropagation();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);
        if (e.dataTransfer.files && e.dataTransfer.files.length) {
            if (onFileSelect) onFileSelect(e.dataTransfer.files[0]);
        }
    };

    const handleClick = () => {
        fileInputRef.current.click();
    };

    return (
        <div
            className={`${styles.container} ${dragActive ? styles.dragActive : ''}`}
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDragOver={handleDragOver}
            onDrop={handleDrop}
        >
            <input
                type="file"
                ref={fileInputRef}
                className={styles.fileInput}
                onChange={handleFileChange}
            />
            <div className={styles.content}>
                <div className={styles.image}>
                    <img src={image} alt='upload' />
                </div>
                <div className={styles.text}>
                    Перетягніть зображення сюди або <br />
                    <span
                        className={styles.link}
                        role="button"
                        tabIndex={0}
                        onClick={handleClick}
                        onKeyDown={(e) => {
                            if (e.key === 'Enter' || e.key === ' ') {
                                handleClick();
                            }
                        }}
                    >
                        завантажте файл
                    </span>

                </div>
            </div>
        </div>
    );
}

export default FilePicker;
