import React from 'react';

const CustomScrollbar = React.forwardRef(({ style, ...props }, ref) => {
  return (
    <div
      ref={ref}
      style={{
        ...style,
        width: 8,
        right: 10,
        borderRadius: 4,
        backgroundColor: 'transparent', // фон вокруг ползунка прозрачный
        position: 'absolute',
        top: 0,
        bottom: 0,
        // добавим небольшой отступ справа
        marginRight: 10,
        // чтобы мышь ловила событие только на ползунок, а не весь трек
        cursor: 'pointer',
        // overflow hidden, чтобы не было скроллбаров внутри
        overflow: 'hidden',
      }}
      {...props}
    >
      {/* сам ползунок — дочерний элемент с 100% шириной */}
      <div
        style={{
          width: '100%',
          borderRadius: 4,
          backgroundColor: '#bbb',
          transition: 'background-color 0.3s',
          // высоту и позицию ползунка virtuoso сам управляет стилями
          height: '100%',
        }}
        className="custom-scrollbar-thumb"
      />
      <style>{`
        .custom-scrollbar-thumb:hover {
          background-color: #888 !important;
        }
      `}</style>
    </div>
  );
});

export default CustomScrollbar;
