import FilePicker from '../../file-picker/FilePicker';
import ModalChatContainer from '../../modal-chat-container/ModalChatContainer';
import styles from './FilePickerContainer.module.css';
import React from 'react';
import cross from '../../../../assets/images/message-form/cross.png'

export default function FilePickerContainer() {
    return (
        <>
            {/* <button type="button" onClick={() => fileInputRef.current?.click()}> */}
            <button type="button">
                <img src={cross} alt="attach media" />
            </button>
            <ModalChatContainer>
                <FilePicker />
            </ModalChatContainer>
            
        </>
    )
}
