import React, { useState, useEffect, useRef } from 'react';
import MessageGroupList from '../../../message-items/message-list/components/MessageGroupList';
import styles from '../styles/Chat.module.css';
import { send } from '../../../../hooks/apiService';
import Header from '../../header/components/Header';
import MessageInputContainer from '../../footer/components/MessageInputContainer';

function Chat({ chatId }) {


    // const [chat, setChat] = useState(null);

    // useEffect(() => {
    //     const loadData = async () => {
    //         try {
    //             const res = await getChat(chatId);
    //             setChat(res.data);
    //         } catch (err) {
    //             console.error('Error loading data', err);
    //         }
    //     };

    //     loadData();
    // }, [chatId]);


    // const { messages, currentUser, companion } = chat;
    const [files, setFiles] = useState([]);
    const [message, setMessage] = useState('');
    const [gif, setGif] = useState({});
    const [audioChunks, setAudioChunks] = useState({});

    const selectGif = (gifObject) => {
        setGif(gifObject);
        handleOnSubmit();
    }

    const handleOnSubmit = async (e) => {
        if (e) e.preventDefault();

        let audioFile = null;

        if (audioChunks.length > 0) {
            const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
            audioFile = new File([audioBlob], 'recording.webm', { type: 'audio/webm' });
        }

        try {
            await send(1, 1, message, files, audioFile, gif?.url || gif?.src || '');
            setMessage('');
            setAudioChunks([]);
            setFiles([]);
            setGif('');
        } catch (err) {
            console.error('Ошибка при отправке:', err);
        }
    };

    return (
        <div className={styles.container}>
            <Header companion={{ firstName: 'allo', lastName: 'allo', event: 'hello' }} />
            <MessageGroupList messages={messages} currentUserId={currentUser.id} />
            <MessageInputContainer
                filesControl={{ files, setFiles }}
                messageControl={{ message, setMessage }}
                gifControl={{ gif, selectGif }}
                audioRecordingControl={{ audioChunks, setAudioChunks }}
                onSubmit={handleOnSubmit}
            />
        </div>
    );
}

export default React.memo(Chat);
