import React, { useMemo } from 'react';
import { Virtuoso } from 'react-virtuoso';
import MessageGroup from './MessageGroup';
import DateSeparator from './DateSeparator';
import { formatDateForSeparator, isNewDay } from '../utils/dateUtils';
import styles from '../styles/MessageGroupList.module.css';

function MessageGroupList({ messages, currentUserId }) {
    const renderedGroups = useMemo(() => {
        const elements = [];
        let currentGroup = [];
        let lastUserId = null;
        let lastDate = null;

        for (let i = 0; i < messages.length; i++) {
            const message = messages[i];
            const { user, sentAt, id } = message;
            const userId = user.id;

            const isNewDate = !lastDate || isNewDay(lastDate, sentAt);
            const isNewUser = userId !== lastUserId;

            if (currentGroup.length > 0 && (isNewUser || isNewDate)) {
                elements.push({
                    type: 'group',
                    key: `group-${currentGroup[0].id}`,
                    messages: currentGroup,
                    isLeft: currentGroup[0].user.id !== currentUserId,
                });
                currentGroup = [];
            }

            if (isNewDate) {
                elements.push({
                    type: 'date',
                    key: `date-${id}`,
                    date: sentAt instanceof Date ? formatDateForSeparator(sentAt) : 'Не вказано'
                });
            }

            currentGroup.push(message);
            lastUserId = userId;
            lastDate = sentAt;
        }

        if (currentGroup.length > 0) {
            elements.push({
                type: 'group',
                key: `group-${currentGroup[0].id}`,
                messages: currentGroup,
                isLeft: currentGroup[0].user.id !== currentUserId,
            });
        }

        return elements;
    }, [messages, currentUserId]);

    return (
        <div className={styles.container}>
            <Virtuoso
                data={renderedGroups}
                itemContent={(index, item) => {
                    if (item.type === 'date') {
                        return <DateSeparator key={item.key} date={item.date} />;
                    }
                    return (
                        <MessageGroup
                            key={item.key}
                            messages={item.messages}
                            isLeft={item.isLeft}
                        />
                    );
                }}
                initialTopMostItemIndex={renderedGroups.length - 1}
                followOutput
            />
        </div>
    );
}

export default React.memo(MessageGroupList);
