import React, { useState, useRef, useEffect } from 'react';
import styles from './AudioRecorder.module.css';
import AudioControls from './AudioControls';
import AudioPreview from './AudioPreview';
import AudioTimer from './AudioTimer';

export default function AudioRecorder({ audioRecordingControl }) {
    const [isRecording, setIsRecording] = useState(false);
    const [isPaused, setIsPaused] = useState(false);
    const [mediaRecorder, setMediaRecorder] = useState(null);
    const [recordingTime, setRecordingTime] = useState(0);
    const [audioSrc, setAudioSrc] = useState(null);
    const [previewReady, setPreviewReady] = useState(false);
    const chunks = useRef([]);
    const intervalRef = useRef(null);

    useEffect(() => {
        if (isRecording && !isPaused) {
            intervalRef.current = setInterval(() => {
                setRecordingTime(prev => prev + 1);
            }, 1000);
        } else {
            clearInterval(intervalRef.current);
        }

        return () => clearInterval(intervalRef.current);
    }, [isRecording, isPaused]);

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60).toString().padStart(2, '0');
        const seconds = (time % 60).toString().padStart(2, '0');
        return `${minutes}:${seconds}`;
    };

    const startRecording = async () => {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const recorder = new MediaRecorder(stream);
        setMediaRecorder(recorder);
        recorder.start();
        setIsRecording(true);

        recorder.ondataavailable = e => chunks.current.push(e.data);
        recorder.onstop = () => {
            const blob = new Blob(chunks.current, { type: 'audio/webm' });
            const url = URL.createObjectURL(blob);
            setAudioSrc(url);
            setPreviewReady(true);
        };
    };

    const stopRecording = () => {
        if (mediaRecorder) {
            mediaRecorder.stop();
            setIsRecording(false);
            setIsPaused(false);
        }
    };

    const pauseRecording = () => {
        mediaRecorder?.pause();
        setIsPaused(true);
    };

    const resumeRecording = () => {
        mediaRecorder?.resume();
        setIsPaused(false);
    };

    const handleSend = async () => {
        const response = await fetch(audioSrc);
        const blob = await response.blob();
        const file = new File([blob], 'audio-message.webm', { type: blob.type });
        audioRecordingControl.setAudio({
            type: 'audio',
            name: file.name,
            id: Date.now(),
            file,
            size: file.size,
            src: audioSrc
        });
        setPreviewReady(false);
        setAudioSrc(null);
    };

    const handleCancel = () => {
        setPreviewReady(false);
        setAudioSrc(null);
        chunks.current = [];
        setRecordingTime(0);
    };

    return (
        <div className={styles.modal}>
            {!isRecording && !previewReady && (
                <button type='button' className={styles.startButton} onClick={startRecording}>
                    üéô –ù–∞—á–∞—Ç—å –∑–∞–ø–∏—Å—å
                </button>
            )}

            {isRecording && !previewReady && (
                <>
                    <AudioTimer isPaused={isPaused} time={recordingTime} />
                    <AudioControls
                        isPaused={isPaused}
                        pauseRecording={pauseRecording}
                        resumeRecording={resumeRecording}
                        stopRecording={stopRecording}
                    />
                </>
            )}

            {previewReady && (
                <AudioPreview
                    audioSrc={audioSrc}
                    onCancel={handleCancel}
                    onSend={handleSend}
                />
            )}
        </div>
    );
}
