import styles from '../styles/MessageGroup.module.css';
import React, { useMemo } from 'react';
import Message from './Message';
import DateSeparator from './DateSeparator';
import { formatDateForSeparator, isNewDay } from '../utils/dateUtils';



const MessageGroup = React.memo(function MessageGroup(props) {
    const { messages, isLeft } = props;
    const renderedMessages = useMemo(() => {
        const elements = [];
        let lastDate = null;

        messages.forEach((msg, index) => {

            const msgDate = msg.sentAt;

            if (!lastDate || isNewDay(lastDate, msgDate)) {
                elements.push(
                    <DateSeparator key={`date-${msg.id}`} date={formatDateForSeparator(msg.sentAt)} />
                );
            }

            elements.push(
                <Message key={msg.id} msg={msg} isLeft={isLeft}/>
            );

            lastDate = msgDate;
        });

        return elements;
    }, [messages, isLeft]);

    return (
        <div className={`${styles.main} ${isLeft ? styles.mainLeft : styles.mainRight} `}>
            {renderedMessages}
        </div>
    );
});

export default MessageGroup;
