import React, { useState, useEffect, useRef } from 'react';
import MessageGroupList from './MessageGroupList';
import EmojiPicker from './EmojiPicker';
import styles from '../styles/Chat.module.css';
import defaultAvatar from '../assets/images/different/default-avatar.png';
import magnifier from '../assets/images/chat-header/magnifier.png';
import phoneReceiver from '../assets/images/chat-header/phone-receiver.png';
import threeStripes from '../assets/images/chat-header/three-stripes.png';
import dotAttached from '../assets/images/chat-header/dot-attached.png';
import dot from '../assets/images/chat-header/dot.png';
import smiley from '../assets/images/message-form/smiley.png';
import gifImg from '../assets/images/message-form/gif.png';
import arrow from '../assets/images/message-form/arrow.png';
import microfon from '../assets/images/message-form/microfon.png';
import cross from '../assets/images/message-form/cross.png';
import GifPicker from './GifPicker';
import MediaPreview from './MediaPreview';
import { useModal } from '../contexts/ModalContext';

function Chat(props) {
    const { messages, currentUser, companion } = props;
    const [isEmojiPickerOpen, setIsEmojiPickerOpen] = useState(false);
    const emojiPickerRef = useRef(null);
    const [isGifPanelOpen, setIsGifPanelOpen] = useState(false);
    const gifPanelRef = useRef(null);
    const [mediaList, setMediaList] = useState([]);
    const [message, setMessage] = useState([]);
    const fileInputRef = useRef(null);
    const [gifList, setGifList] = useState([]);
    const { isModalOpen } = useModal();
    const [isRecording, setIsRecording] = useState(false);
    const [mediaRecorder, setMediaRecorder] = useState(null);
    const [audioChunks, setAudioChunks] = useState([]);

    const handleAudioClick = async () => {
        if (!isRecording) {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
                const recorder = new MediaRecorder(stream);
                const chunks = [];

                recorder.ondataavailable = (e) => {
                    if (e.data.size > 0) chunks.push(e.data);
                };

                recorder.onstop = () => {
                    const audioBlob = new Blob(chunks, { type: 'audio/webm' });
                    const audioUrl = URL.createObjectURL(audioBlob);

                    const newAudio = {
                        id: `${Date.now()}_audio`,
                        type: 'audio',
                        src: audioUrl,
                        file: audioBlob
                    };
                    setMediaList(prev => [...prev, newAudio]);
                };

                recorder.start();
                setMediaRecorder(recorder);
                setAudioChunks(chunks);
                setIsRecording(true);
            } catch (err) {
                console.error('Ошибка при доступе к микрофону', err);
            }
        } else {
            mediaRecorder?.stop();
            setIsRecording(false);
        }
    };





    const [mainEmojis, setMainEmojis] = useState(() => {
        try {
            const saved = localStorage.getItem('mainEmojis');
            return saved ? JSON.parse(saved) : [];
        } catch (e) {
            console.error('Failed to load emojis from localStorage:', e);
            return [];
        }
    });

    const handleImageUpload = (event) => {
        if (mediaList.length < 11) {
            const files = Array.from(event.target.files);
            const newMedia = files.map((file, index) => ({
                id: `${Date.now()}_${index}`,
                type: file.type.startsWith('video') ? 'video' : 'image',
                src: URL.createObjectURL(file),
                file
            }));
            setMediaList((prev) => [...prev, ...newMedia]);
        }
    };

    const handleRemoveMedia = (media) => {
        if (media.type === 'selected_gif') {
            setGifList((prev) => prev.filter((gif) => gif.id !== media.id));
        }
        else {
            setMediaList((prev) => prev.filter((content) => content.id !== media.id));
        }
    };

    const handleGifClick = () => {
        setIsGifPanelOpen((prev) => !prev);
        setIsEmojiPickerOpen(false);
    };


    const textareaRef = useRef(null);
    const handleGifSelect = (gifUrl) => {
        if (gifList.length < 11) {

            const newGif = {
                id: (`${Date.now()}_gif`),
                type: 'selected_gif',
                src: gifUrl
            };
            setGifList((prev) => [...prev, newGif]);
            setIsGifPanelOpen(false);
        }
    };


    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                gifPanelRef.current &&
                !gifPanelRef.current.contains(event.target)
            ) {
                setIsGifPanelOpen(false);
            }
        };

        if (isGifPanelOpen) {
            document.addEventListener('mousedown', handleClickOutside);
        }

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isGifPanelOpen]);


    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                emojiPickerRef.current &&
                !emojiPickerRef.current.contains(event.target)
            ) {
                setIsEmojiPickerOpen(false);
            }
        };

        if (isEmojiPickerOpen) {
            document.addEventListener('mousedown', handleClickOutside);
        } else {
            document.removeEventListener('mousedown', handleClickOutside);
        }

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isEmojiPickerOpen]);

    const handleInput = () => {
        const textarea = textareaRef.current;
        if (textarea) {
            textarea.style.height = 'auto';
            textarea.style.height = textarea.scrollHeight + 'px';
        }
    };

    const handleEmojiSelect = (emoji) => {
        if (textareaRef.current) {
            const textarea = textareaRef.current;
            const start = textarea.selectionStart;
            const end = textarea.selectionEnd;
            const value = textarea.value;
            textarea.value = value.slice(0, start) + emoji + value.slice(end);
            textarea.focus();
            textarea.selectionStart = textarea.selectionEnd = start + emoji.length;
            handleInput();
            setMessage(textarea.value);
            setMainEmojis((prevMainEmojis) => {
                const newList = [emoji, ...prevMainEmojis.filter(e => e !== emoji)];
                const finalList = newList.slice(0, 44);

                try {
                    localStorage.setItem('mainEmojis', JSON.stringify(finalList));
                } catch (e) {
                    console.error('Failed to save emojis to localStorage:', e);
                }

                return finalList;
            });
        }
    };

    const handleEmojiClick = () => {
        setIsEmojiPickerOpen((prev) => !prev);
    };

    const [isHovered, setIsHovered] = useState(false);
    const [isAttached, setIsAttached] = useState(true);
    const timeoutRef = useRef(null);

    const handleMouseEnter = () => {
        clearTimeout(timeoutRef.current);
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        timeoutRef.current = setTimeout(() => {
            setIsHovered(false);
        }, 1500);
    };

    const handleAttachClick = () => {
        setIsAttached((prev) => !prev);
    }

    return (
        <div className={styles.container}>
            <div
                className={`${styles.header} ${isModalOpen ? styles.hidden : ''}`}
                onMouseEnter={!isAttached ? handleMouseEnter : null}
                onMouseLeave={!isAttached ? handleMouseLeave : null}
            >
                <div
                    className={`${styles.headerItem} ${isHovered || isAttached ? styles.visible : ''}`}
                >
                    <div className={styles.info}>
                        <div className={styles.image}>
                            <img src={companion?.avatar || defaultAvatar} alt='Avatar' />
                        </div>
                        <div className={styles.descr}>
                            <div className={styles.name}>
                                {companion?.firstName} {companion?.lastName}
                            </div>
                            <div className={styles.event}>
                                {companion.event}
                            </div>
                        </div>
                    </div>
                    <div className={styles.images}>
                        <img src={phoneReceiver} alt='Call' />
                        <img src={magnifier} alt='Search' />
                        <img src={threeStripes} alt='Additional' />
                    </div>
                    <div className={styles.btnAttach}>
                        {isAttached ? <img src={dotAttached} alt='attach' onClick={handleAttachClick} /> : <img src={dot} alt='attach' onClick={handleAttachClick} />}

                    </div>
                </div>
            </div>

            <MessageGroupList messages={messages} currentUserId={currentUser.id} />



            <div className={`${styles.messageInputContainer} ${isModalOpen ? styles.hidden : ''}`}>
                {(mediaList.length > 0 || gifList.length > 0) && (
                    <div className={styles.mediaPreview}>
                        <MediaPreview mediaList={mediaList} gifList={gifList} onRemove={handleRemoveMedia} />
                    </div>
                )}
                <form name="messageInput" className={styles.messageForm}>
                    <textarea
                        ref={textareaRef}
                        name="message"
                        className={styles.messageInput}
                        autoComplete="off"
                        placeholder="Напишіть ваше повідомлення"
                        onInput={handleInput}
                        rows={1}
                        onChange={(e) => setMessage(e.target.value)}
                    />
                    <div className={styles.messageImages}>
                        <button type="button" onClick={handleEmojiClick}>
                            <img src={smiley} alt="emoji" />
                        </button>
                        {isEmojiPickerOpen && (
                            <div className={styles.emojiWrapper} ref={emojiPickerRef}>
                                <div className={styles.emojiContainer}>
                                    <EmojiPicker onSelect={handleEmojiSelect} mainEmojis={mainEmojis} />
                                </div>
                            </div>
                        )}
                        <button type="button" onClick={handleGifClick}>
                            <img src={gifImg} alt="gif" />
                        </button>

                        {isGifPanelOpen && (
                            <div className={styles.emojiWrapper} ref={gifPanelRef}>
                                <div className={styles.emojiContainer}>
                                    <GifPicker onSelect={handleGifSelect} setIsGifPanelOpen={setIsGifPanelOpen}/>
                                </div>
                            </div>
                        )}

                        <button type="button" onClick={() => fileInputRef.current?.click()}>
                            <img src={cross} alt="attach media" />
                        </button>
                        <input
                            type="file"
                            accept="image/*,video/*"
                            multiple
                            ref={fileInputRef}
                            style={{ display: 'none' }}
                            onChange={handleImageUpload}
                        />
                        <button type="button" onClick={handleAudioClick}>
                            <img src={microfon} alt="audio" style={{ filter: isRecording ? 'brightness(0.5)' : 'none' }} />
                        </button>

                        <button type="submit"><img src={arrow} alt="send" /></button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default React.memo(Chat);
