import React, { useRef, useState, useEffect } from 'react';
import styles from './CustomAudioPlayer.module.css';
import pause from '../../../assets/images/custom-audio-player/pause.png';
import play from '../../../assets/images/custom-audio-player/play.png';
import AudioTimer from '../audio-recorder/AudioTimer';

function CustomAudioPlayer({ src }) {
  const audioRef = useRef(null);

  const [isReady, setIsReady]         = useState(false);
  const [isPlaying, setIsPlaying]     = useState(false);
  const [duration, setDuration]       = useState(0);
  const [currentTime, setCurrentTime] = useState(0);

  // Загружаем метаданные (duration) и даём право на Play
  const onLoadedMetadata = () => {
    const audio = audioRef.current;
    if (audio && !isNaN(audio.duration) && audio.duration > 0) {
      setDuration(audio.duration);
      setIsReady(true);
    }
  };

  // Навешиваем также на canplay: иногда duration доступен только здесь
  const onCanPlay = () => {
    onLoadedMetadata();
  };

  // Обновляем текущее время при воспроизведении
  const onTimeUpdate = () => {
    const audio = audioRef.current;
    if (!audio) return;
    const ct = isNaN(audio.currentTime) ? 0 : audio.currentTime;
    setCurrentTime(ct);
  };

  // Play/Pause работает только когда isReady===true
  const togglePlay = () => {
    if (!isReady) return;
    const audio = audioRef.current;
    if (!audio) return;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  // Кликаем по прогрессбару, чтобы перемотать
  const onProgressClick = e => {
    if (!isReady) return;
    const audio = audioRef.current;
    if (!audio || duration <= 0) return;
    const { left, width } = e.currentTarget.getBoundingClientRect();
    const clickX  = e.clientX - left;
    const ratio   = clickX / width;
    audio.currentTime = ratio * audio.duration;
    setCurrentTime(audio.currentTime);
  };

  // При смене src сбрасываем всё
  useEffect(() => {
    setIsReady(false);
    setIsPlaying(false);
    setDuration(0);
    setCurrentTime(0);
  }, [src]);

  // Вычисляем процент для прогрессбара
  const progressPercent =
    duration > 0
      ? (currentTime / duration) * 100
      : 0;

  return (
    <div className={styles.audioPlayer}>
      <audio
        ref={audioRef}
        src={src}
        preload="metadata"
        onLoadedMetadata={onLoadedMetadata}
        onCanPlay={onCanPlay}
        onTimeUpdate={onTimeUpdate}
        onEnded={() => setIsPlaying(false)}
      />

      <button
        type="button"
        onClick={togglePlay}
        className={styles.playButton}
        disabled={!isReady}
        aria-label={
          !isReady
            ? 'Loading…'
            : isPlaying
              ? 'Pause'
              : 'Play'
        }
      >
        {isPlaying
          ? <img src={pause} alt="Pause" className={styles.play} />
          : <img src={play}  alt={isReady ? "Play" : "Loading"} />}
      </button>

      <div
        className={styles.progressBar}
        role="progressbar"
        aria-valuemin={0}
        aria-valuemax={duration}
        aria-valuenow={currentTime}
        tabIndex={isReady ? 0 : -1}
        onClick={onProgressClick}
      >
        <div
          className={styles.progress}
          style={{ width: `${progressPercent}%` }}
        />
      </div>

      <AudioTimer
        isPaused={!isPlaying}
        time={currentTime}
        withDot={false}
      />
    </div>
  );
}

export default CustomAudioPlayer;
