import React from 'react';
import styles from '../styles/MediaPreview.module.css';

function MediaPreview(props) {

    const { mediaList, gifList, onRemove } = props

    return (
        <div className={styles.previewContainer}>
            {mediaList.map((media) => (
                <div key={media.id} className={styles.previewItem}>
                    {media.type === 'image' && <img src={media.src} alt="preview" className={styles.previewMedia} />}
                    {(media.type === 'video' || media.type === 'gif') && (
                        <video
                            src={media.src}
                            className={styles.previewMedia}
                            autoPlay
                            loop
                            muted
                            playsInline
                        />
                    )}
                    <button
                        type="button"
                        className={styles.removeButton}
                        onClick={() => onRemove(media)}
                        aria-label="Remove media"
                    >
                        ×
                    </button>
                </div>
            ))}
            {gifList.map((media) => (
                <div key={media.id} className={styles.previewItem}>
                    {(type === 'selected_gif') && (
                        <video
                            src={media.src}
                            className={styles.previewMedia}
                            autoPlay
                            loop
                            muted
                            playsInline
                        />
                    )}
                    <button
                        type="button"
                        className={styles.removeButton}
                        onClick={() => onRemove(media)}
                        aria-label="Remove media"
                    >
                        ×
                    </button>
                </div>
            ))}
        </div>
    );
}

export default React.memo(MediaPreview);
