import React from 'react';
import styles from '../../styles/chat-items/Header.module.css';
import HeaderItem from './HeaderItem';
import useHeaderVisibility from './useHeaderVisibility';
import { useModal } from '../contexts/ModalContext';

const Header = (props) => {
    const { isModalOpen } = useModal();
    const { companion } = props;
    const headerContent = useMemo(() => (
        <header
            className={`${styles.header} ${isModalOpen ? styles.hidden : ''}`}
            onMouseEnter={!isAttached ? handleMouseEnter : null}
            onMouseLeave={!isAttached ? handleMouseLeave : null}
        >
            <div
                className={`${styles.headerItem} ${isHovered || isAttached ? styles.visible : ''}`}
            >
                <HeaderItem />
            </div>
        </header>
    ), [companion]);
    return headerContent;
};

export default React.memo(Header);
