import React, { useState, useMemo, useRef, useCallback } from 'react';
import styles from '../../styles/chat-items/Header.module.css';
import HeaderItem from './HeaderItem';
import { useModal } from '../../contexts/ModalContext';

const Header = (props) => {
    const [isHovered, setIsHovered] = useState(false);
    const [isAttached, setIsAttached] = useState(true);
    const timeoutRef = useRef(null);

    const handleMouseEnter = () => {
        clearTimeout(timeoutRef.current);
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        timeoutRef.current = setTimeout(() => {
            setIsHovered(false);
        }, 1500);
    };

    const handleAttachClick = useCallback(() => {
        setIsAttached(prev => !prev);
    }, []);

    const { isModalOpen } = useModal();
    const { companion } = props;

    const headerContent = useMemo(() => (
        <header
            className={`${styles.header} ${isModalOpen ? styles.hidden : ''}`}
            onMouseEnter={!isAttached ? handleMouseEnter : null}
            onMouseLeave={!isAttached ? handleMouseLeave : null}
        >
            <HeaderItem
                handleAttachClick={handleAttachClick}
                companion={companion}
                isAttached={isAttached}
                isHovered={isHovered}
            />
        </header>
    ), [companion, isModalOpen, isAttached, isHovered, handleAttachClick]);

    return headerContent;
};

export default React.memo(Header);
