import { useRef, useMemo, memo } from 'react';
import styles from './EmojiPicker.module.css';
import emojiData from 'unicode-emoji-json/data-by-group';
import DataSeparator from '../data-separator/DataSeparator';
import animal from '../../../assets/images/emojis/animal.png';
import ball from '../../../assets/images/emojis/ball.png';
import car from '../../../assets/images/emojis/car.png';
import diff from '../../../assets/images/emojis/diff.png';
import flag from '../../../assets/images/emojis/flag.png';
import food from '../../../assets/images/emojis/food.png';
import person from '../../../assets/images/emojis/person.png';
import smiley from '../../../assets/images/emojis/smiley.png';
import watch from '../../../assets/images/emojis/watch.png';
import bulb from '../../../assets/images/emojis/bulb.png';

const EmojiPicker = ({ onSelect, mainEmojis }) => {
  const groupRefs = useRef({});

  const emojis = useMemo(() => Object.entries(emojiData), []);

  const separatorStyles = {
    styleContainer: {
      width: '100%',
      color: '#2C2F36',
      marginTop: '8px'
    },
    styleText: {
      fontSize: '14px',
      fontWeight: '400',
      fontFamily: '"Raleway", sans-serif',
      color: '#ADADBD',
      margin: '0 15px',
      letterSpacing: '0%'
    },
    lineColor: '#ADADBD'
  };

  // Подготовим единый плоский массив с элементами для виртуализации:
  const items = useMemo(() => {
    const list = [];

    if (mainEmojis?.length > 0) {
      list.push({
        type: 'separator',
        key: 'frequent-separator',
        label: 'Часто використовувані',
        groupKey: 'frequent',
      });
      mainEmojis.forEach((emoji) => {
        list.push({
          type: 'emoji',
          key: `frequent-${emoji}`,
          emoji,
          label: emoji,
          groupKey: 'frequent',
        });
      });
    }

    emojis.forEach(([groupKey, data]) => {
      list.push({
        type: 'separator',
        key: `separator-${groupKey}`,
        label: data.name,
        groupKey,
      });
      data.emojis.forEach(({ emoji, name }) => {
        list.push({
          type: 'emoji',
          key: `emoji-${groupKey}-${emoji}`,
          emoji,
          label: name,
          groupKey,
        });
      });
    });

    return list;
  }, [mainEmojis, emojis]);

  const itemContent = (index) => {
    const item = items[index];
    if (item.type === 'separator') {
      return (
        <div
          ref={(el) => {
            if (el) groupRefs.current[item.groupKey] = el;
          }}
          className={styles.groupContainer}
        >
          <DataSeparator
            separatorStyles={separatorStyles}
            data={item.label}
          />
        </div>
      );
    }

    if (item.type === 'emoji') {
      return (
        <button
          key={item.key}
          onClick={() => onSelect(item.emoji)}
          className={styles.emoji}
          type="button"
          aria-label={item.label}
          title={item.label}
        >
          <span>{item.emoji}</span>
        </button>
      );
    }
    return null;
  };

  return (
    <div className={styles.container}>
      {/* Навигация можно оставить без изменений */}
      <div className={styles.nav}>
        {mainEmojis?.length > 0 && (
          <button
            className={styles.emoji}
            onClick={() => groupRefs.current['frequent']?.scrollIntoView({ behavior: 'smooth', block: 'start' })}
            type="button"
            title="Часто використовувані"
          >
            <div>
              <img src={watch} alt="watch" />
            </div>
          </button>
        )}
        {emojis.map(([groupKey, data]) => (
          <button
            key={groupKey}
            className={styles.emoji}
            onClick={() => groupRefs.current[groupKey]?.scrollIntoView({ behavior: 'smooth', block: 'start' })}
            type="button"
            title={data.name}
          >
            <div>
              <img src={whatImage(data.name)} alt={data.name} />
            </div>
          </button>
        ))}
      </div>

      {/* Виртуализированный список */}
      <div className={styles.emojiSection}>
        <Virtuoso
          totalCount={items.length}
          itemContent={itemContent}
          style={{ height: 350, width: '100%' }} // Размеры под твой контейнер
        />
      </div>
    </div>
  );
};

// Не забудь оставить функцию whatImage, она у тебя была:
const whatImage = (groupName) => {
  if (groupName === 'Smileys & Emotion') return smiley;
  if (groupName === 'People & Body') return person;
  if (groupName === 'Animals & Nature') return animal;
  if (groupName === 'Food & Drink') return food;
  if (groupName === 'Travel & Places') return car;
  if (groupName === 'Activities') return ball;
  if (groupName === 'Objects') return bulb;
  if (groupName === 'Symbols') return diff;
  if (groupName === 'Flags') return flag;
};

export default memo(EmojiPicker);
