import React from 'react';
import styles from '../styles/FilePreviewArea.module.css';

const FilePreviewArea = ({ files, onDelete, onBlur, onBlurAll, onClose }) => {
    return (
        <div className={styles.overlay}>
            <div className={styles.header}>
                <button className={styles.blurAllBtn} onClick={onBlurAll} type="button">
                    Размыть все
                </button>
                <button className={styles.closeBtn} onClick={onClose} type="button">
                    Закрыть
                </button>
            </div>
            <div className={styles.filesContainer}>
                {files.map((file, index) => {
                    const isImage = file.type.startsWith('image/');
                    const isVideo = file.type.startsWith('video/');
                    const url = URL.createObjectURL(file);

                    return (
                        <div
                            key={file.name + index}
                            className={`${styles.fileWrapper} ${isImage ? styles.imageWrapper : styles.fileFullWidth
                                }`}
                        >
                            {isImage && (
                                <img
                                    src={url}
                                    alt={file.name}
                                    className={styles.filePreview}
                                />
                            )}
                            {isVideo && (
                                <video controls className={styles.filePreview}>
                                    <source src={url} type={file.type} />
                                    Ваш браузер не поддерживает видео.
                                </video>
                            )}
                            {!isImage && !isVideo && (
                                <div className={styles.fileOther}>
                                    <p>{file.name}</p>
                                    <p>({file.type || 'неизвестный формат'})</p>
                                </div>
                            )}
                            <div className={styles.buttons}>
                                <button
                                    className={styles.btn}
                                    onClick={() => onDelete(index)}
                                    type="button"
                                >
                                    Удалить
                                </button>
                                {isImage && (
                                    <button
                                        className={styles.btn}
                                        onClick={() => onBlur(index)}
                                        type="button"
                                    >
                                        Размыть
                                    </button>
                                )}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default FilePreviewArea;
