import React, { useState, useRef, useEffect } from 'react';
import styles from './AudioRecorder.module.css';
import AudioControls from './AudioControls';
import AudioPreview from './AudioPreview';
import AudioTimer from './AudioTimer';
import microfon from '../../../assets/images/message-form/microfon.png';
import microfonSelected from '../../../assets/images/message-form/microfon-selected.png';

export default function AudioRecorder({ audioRecordingControl, onSend }) {
    const [isRecorderOpen, setIsRecorderOpen] = useState(false);
    const [isRecording, setIsRecording] = useState(false);
    const [isPaused, setIsPaused] = useState(false);
    const [mediaRecorder, setMediaRecorder] = useState(null);
    const [recordingTime, setRecordingTime] = useState(0);
    const [audioSrc, setAudioSrc] = useState(null);
    const [previewReady, setPreviewReady] = useState(false);
    const chunks = useRef([]);
    const intervalRef = useRef(null);

    console.log(audioRecordingControl);

    useEffect(() => {
        if (isRecording && !isPaused) {
            intervalRef.current = setInterval(() => {
                setRecordingTime(prev => prev + 1);
            }, 1000);
        } else {
            clearInterval(intervalRef.current);
        }

        return () => clearInterval(intervalRef.current);
    }, [isRecording, isPaused]);

    const startRecording = async () => {
        if (!isRecorderOpen) {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            const recorder = new MediaRecorder(stream);
            setMediaRecorder(recorder);
            recorder.start();
            setIsRecording(true);
            setIsRecorderOpen(true);

            recorder.ondataavailable = e => chunks.current.push(e.data);
            recorder.onstop = () => {
                const blob = new Blob(chunks.current, { type: 'audio/webm' });
                const url = URL.createObjectURL(blob);
                setAudioSrc(url);
                setPreviewReady(true);
            };
        }
        else {
            handleCancel();
        }
    };

    const stopRecording = () => {
        if (mediaRecorder) {
            mediaRecorder.stop();
            setIsRecording(false);
            setIsPaused(false);
        }
    };

    const pauseRecording = () => {
        mediaRecorder?.pause();
        setIsPaused(true);
    };

    const resumeRecording = () => {
        mediaRecorder?.resume();
        setIsPaused(false);
    };

    const handleSend = async () => {
        stopRecording();
        const response = await fetch(audioSrc);
        const blob = await response.blob();
        const file = new File([blob], 'audio-message.webm', { type: blob.type });
        audioRecordingControl.setAudio({
            type: file.type,
            name: file.name,
            id: 'audio_' + Date.now(),
            file: file,
            size: file.size,
            src: audioSrc
        });
        setPreviewReady(false);
        setAudioSrc(null);
        setIsRecorderOpen(false);
        setRecordingTime(0);
        onSend();
    };

    const handleCancel = () => {
        setPreviewReady(false);
        setAudioSrc(null);
        chunks.current = [];
        setRecordingTime(0);
        setIsRecorderOpen(false);
        setIsRecording(false);
        audioRecordingControl.setAudio({});
    };

    return (
        <>

            <button type='button' onClick={startRecording}>
                {isRecorderOpen ?
                    (<img src={microfonSelected} alt='stop audio' />)
                    :
                    (<img src={microfon} alt='record audio' />)
                }
            </button>

            {isRecorderOpen &&
                (<div className={styles.modal}>
                    {isRecording && !previewReady && (
                        <>
                            <AudioControls
                                isPaused={isPaused}
                                pauseRecording={pauseRecording}
                                resumeRecording={resumeRecording}
                                stopRecording={stopRecording}
                                cancelRecording={handleCancel}
                                onSend={handleSend}
                            ><AudioTimer isPaused={isPaused} time={recordingTime} /></AudioControls>
                        </>
                    )}

                    {previewReady && (
                        <AudioPreview
                            audioSrc={audioSrc}
                            onCancel={handleCancel}
                            onSend={handleSend}
                        />
                    )}
                </div>)
            }
        </>
    );
}
