import React from "react";
import { useRef, useState, useEffect } from "react";
import styles from './MessageInput.module.css';
import arrow from '../../../assets/images/message-form/arrow.png';
import microfon from '../../../assets/images/message-form/microfon.png';
import microfonSelected from '../../../assets/images/message-form/microfon-selected.png';
import cross from '../../../assets/images/message-form/cross.png';
import EmojiPickerContainer from "../picker-containers/emoji-picker-container/EmojiPickerContainer";
import GifPickerContainer from "../picker-containers/gif-picker-container/GifPickerContainer";
import FilePickerContainer from "../picker-containers/file-picker-container/FilePickerContainer";

function MessageInput({ messageControl, filesControl, gifControl, audioRecordingControl, content }) {

    const [isRecording, setIsRecording] = useState(false);
    const [mediaRecorder, setMediaRecorder] = useState(null);
    const { message, setMessage } = messageControl;
    const { gif, selectGif } = gifControl;
    const { audioChunks, setAudioChunks } = audioRecordingControl;
    const fileInputRef = useRef(null);

    const hasGif = content.some(item => item.includes('gif'));
    const hasAudio = content.some(item => item.includes('audio'));

    // const handleAudioClick = async () => {
    //     if (!isRecording) {
    //         try {
    //             const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    //             const recorder = new MediaRecorder(stream);
    //             const chunks = [];

    //             recorder.ondataavailable = (e) => {
    //                 if (e.data.size > 0) chunks.push(e.data);
    //             };

    //             recorder.onstop = () => {
    //                 const audioBlob = new Blob(chunks, { type: 'audio/webm' });
    //                 const audioUrl = URL.createObjectURL(audioBlob);

    //                 const newAudio = {
    //                     id: `${Date.now()}_audio`,
    //                     type: 'audio',
    //                     src: audioUrl,
    //                     file: audioBlob
    //                 };
    //                 setMediaList(prev => [...prev, newAudio]);
    //             };

    //             recorder.start();
    //             setMediaRecorder(recorder);
    //             setAudioChunks(chunks);
    //             setIsRecording(true);
    //         } catch (err) {
    //             console.error('Ошибка при доступе к микрофону', err);
    //         }
    //     } else {
    //         mediaRecorder?.stop();
    //         setIsRecording(false);
    //     }
    // };
    // const handleRemoveMedia = (media) => {
    //     if (media.type === 'selected_gif') {
    //         setGifList((prev) => prev.filter((gif) => gif.id !== media.id));
    //     }
    //     else {
    //         setMediaList((prev) => prev.filter((content) => content.id !== media.id));
    //     }
    // };
    const textareaRef = useRef(null);

    const handleInput = () => {
        const textarea = textareaRef.current;
        if (textarea) {
            textarea.style.height = 'auto';
            textarea.style.height = textarea.scrollHeight + 'px';
        }
    };

    // const handleImageUpload = (event) => {
    //     if (mediaList.length < 11) {
    //         const files = Array.from(event.target.files);
    //         const newMedia = files.map((file, index) => ({
    //             id: `${Date.now()}_${index}`,
    //             type: file.type.startsWith('video') ? 'video' : 'image',
    //             src: URL.createObjectURL(file),
    //             file
    //         }));
    //         setMediaList((prev) => [...prev, ...newMedia]);
    //     }
    // };



    return (<form name="messageInput" className={styles.messageForm}>
        <textarea
            ref={textareaRef}
            name="message"
            className={styles.messageInput}
            autoComplete="off"
            placeholder="Напишіть ваше повідомлення"
            onInput={handleInput}
            rows={1}
            onChange={(e) => setMessage(e.target.value)}
        />
        <div className={styles.messageImages}>

            {content.some(item => item.includes('emoji')) &&
                (<EmojiPickerContainer setMessage={setMessage} textareaRef={textareaRef} handleInput={handleInput} />)
            }
            {content.some(item => item.includes('gif')) &&
                (<GifPickerContainer setGif={selectGif} />)
            }
            {content.some(item => item.includes('upload')) &&
                (<FilePickerContainer filesControl={filesControl} messageControl={messageControl} />)
            }

            {/* <button type="button" onClick={handleAudioClick}>
                {isRecording ? (<img src={microfonSelected} alt="audio" />) : (<img src={microfon} alt="audio" />)}
            </button> */}

            <button type="submit"><img src={arrow} alt="send" /></button>
        </div>
    </form>
    )
}

export default MessageInput;
