import React from 'react';

const CustomScrollbar = React.forwardRef(({ style, ...props }, ref) => (
  <div
    ref={ref}
    style={{
      ...style,
      width: 8,
      borderRadius: 4,
      backgroundColor: '#bbb',
      position: 'absolute',
      top: 0,
      bottom: 0,
      right: 10, // отодвигаем от правого края на 10px
      cursor: 'pointer',
      transition: 'background-color 0.3s',
      // Убираем marginRight — используем right для позиционирования
    }}
    onMouseEnter={(e) => {
      e.currentTarget.style.backgroundColor = '#888';
    }}
    onMouseLeave={(e) => {
      e.currentTarget.style.backgroundColor = '#bbb';
    }}
    {...props}
  />
));

export default CustomScrollbar;
