import React, { useState, useMemo } from 'react';
import styles from './FilePreviewArea.module.css';
import MediaForPreview from '../media/MediaForPreview';

function FilePreviewArea({ filesControl, onClose }) {
    const { files, setFiles } = filesControl;
    const [blurAll, setBlurAll] = useState(false);

    const imageMedia = useMemo(
        () => files.filter(item => item.type.startsWith('image') || item.type.startsWith('video')),
        [files]
    );

    const otherFiles = useMemo(
        () => files.filter(item => !item.type.startsWith('image') && !item.type.startsWith('video')),
        [files]
    );

    const handleRemove = (idToRemove) => {
        setFiles(prev => prev.filter(f => f.id !== idToRemove));
    };

    const handleToggleBlur = (idToToggle) => {
        setFiles(prev =>
            prev.map(f =>
                f.id === idToToggle
                    ? { ...f, isBloored: !f.isBloored }
                    : f
            )
        );
    };

    const toggleBlurAll = () => {
        setBlurAll(prev => !prev);
        setFiles(prev =>
            prev.map(f =>
                (f.type.startsWith('image') || f.type.startsWith('video'))
                    ? { ...f, isBloored: blurAll }
                    : f
            )
        );
    };

    return (
        <div className={styles.overlay}>
            <div className={styles.header}>
                <button className={styles.button} onClick={toggleBlurAll}>
                    {blurAll ? 'Remove Blur' : 'Blur All Images'}
                </button>
                <button className={styles.closeButton} onClick={onClose}>âœ•</button>
            </div>

            <div className={styles.content}>
                {imageMedia.length > 0 && (
                    <div className={styles.mediaBlock}>
                        <MediaForPreview
                            media={imageMedia}
                            onRemove={handleRemove}
                            onBlurToggle={handleToggleBlur}
                        />
                    </div>
                )}

                {otherFiles.length > 0 && (
                    <div className={styles.filesBlock}>
                        <h3>Other files</h3>
                        <ul>
                            {otherFiles.map(f => (
                                <li key={f.id}>
                                    {f.src}
                                    <button onClick={() => handleRemove(f.id)}>Remove</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default FilePreviewArea;
