import styles from '../styles/EmojiPicker.module.css';
import emojiData from 'unicode-emoji-json/data-by-group';

const EmojiPicker = ({ onSelect }) => {

    const emojis = Object.entries(emojiData);

    const renderEmojis = () => {
        const emojiList = [];
        emojis.forEach(([emojiGroup, data]) => {
            console.log(data.emojis)
            emojiList.push(
                <div>
                    <p>{emojiGroup}</p>

                    {
                        data.map((emojiData) => (
                            <button
                                key={emojiData.code}
                                onClick={() => onSelect(emojiData.emoji)}
                                className={styles.emoji}
                                type="button"
                            >{emojiData.emoji}
                            </button>
                        ))}
                </div>
            );
        });
        console.log(emojiList)
        return emojiList;
    };
    return (
        <div className={styles.container}>
            {renderEmojis()}
        </div>
    );
};

export default EmojiPicker;
