import { useRef, useMemo, memo } from 'react';
import { GroupedVirtuoso } from 'react-virtuoso';
import styles from './EmojiPicker.module.css';
import emojiData from 'unicode-emoji-json/data-by-group';
import DataSeparator from '../data-separator/DataSeparator';
import animal from '../../../assets/images/emojis/animal.png';
import ball from '../../../assets/images/emojis/ball.png';
import car from '../../../assets/images/emojis/car.png';
import diff from '../../../assets/images/emojis/diff.png';
import flag from '../../../assets/images/emojis/flag.png';
import food from '../../../assets/images/emojis/food.png';
import person from '../../../assets/images/emojis/person.png';
import smiley from '../../../assets/images/emojis/smiley.png';
import watch from '../../../assets/images/emojis/watch.png';
import bulb from '../../../assets/images/emojis/bulb.png';



const EmojiPicker = (props) => {
    const virtuosoRef = useRef(null);
    const { onSelect, mainEmojis } = props;

    const { groupLabels, emojisFlat } = useMemo(() => {
        const groups = [];
        const emojis = [];

        if (mainEmojis?.length > 0) {
            groups.push('Часто використовувані');
            emojis.push(...mainEmojis);
        }

        for (const [groupKey, data] of Object.entries(emojiData)) {
            groups.push(data.name);
            emojis.push(...data.emojis.map(e => e.emoji));
        }

        return { groupLabels: groups, emojisFlat: emojis };
    }, [mainEmojis]);

    const groupImages = useMemo(() => {
        const map = {};
        for (const data of Object.values(emojiData)) {
            if (data.name === 'Smileys & Emotion') map[data.name] = smiley;
            else if (data.name === 'People & Body') map[data.name] = person;
            else if (data.name === 'Animals & Nature') map[data.name] = animal;
            else if (data.name === 'Food & Drink') map[data.name] = food;
            else if (data.name === 'Travel & Places') map[data.name] = car;
            else if (data.name === 'Activities') map[data.name] = ball;
            else if (data.name === 'Objects') map[data.name] = bulb;
            else if (data.name === 'Symbols') map[data.name] = diff;
            else if (data.name === 'Flags') map[data.name] = flag;
        }
        map['Часто використовувані'] = watch;
        return map;
    }, []);

    const groupCounts = useMemo(() => {
        const counts = [];
        if (mainEmojis?.length > 0) counts.push(mainEmojis.length);
        for (const data of Object.values(emojiData)) {
            counts.push(data.emojis.length);
        }
        return counts;
    }, [mainEmojis]);

    const groupHeader = (index) => (
        <div className={styles.groupContainer}>
            <DataSeparator
                separatorStyles={{
                    styleContainer: { width: '100%', color: '#2C2F36', marginTop: '8px' },
                    styleText: {
                        fontSize: '14px',
                        fontWeight: '400',
                        fontFamily: '"Raleway", sans-serif',
                        color: '#ADADBD',
                        margin: '0 15px',
                        letterSpacing: '0%',
                    },
                    lineColor: '#ADADBD',
                }}
                data={groupLabels[index]}
            />
        </div>
    );

    const emojiIndexToData = useMemo(() => {
        const map = new Map();

        if (mainEmojis?.length > 0) {
            mainEmojis.forEach(e => map.set(e, e));
        }

        for (const data of Object.values(emojiData)) {
            data.emojis.forEach(e => {
                map.set(e.emoji, e.name);
            });
        }

        return map;
    }, [mainEmojis]);

    const emojiRenderer = (index) => {
        const emoji = emojisFlat[index];
        const name = emojiIndexToData.get(emoji) || emoji;

        return (
            <button
                key={emoji}
                onClick={() => onSelect(emoji)}
                className={styles.emoji}
                type="button"
                aria-label={name}
                title={name}
            >
                <div><span>{emoji}</span></div>
            </button>
        );
    };

    return (
        <div className={styles.container}>
            <div className={styles.nav}>
                {mainEmojis?.length > 0 && (
                    <button
                        className={styles.emoji}
                        onClick={() => {
                            virtuosoRef.current.scrollToIndex({ index: 0, align: 'start' });
                        }}
                        type="button"
                        title="Часто використовувані"
                    >
                        <div><img src={watch} alt="watch" /></div>
                    </button>
                )}
                {Object.values(emojiData).map(data => (
                    <button
                        key={data.name}
                        className={styles.emoji}
                        onClick={() => {
                            const idx = groupLabels.indexOf(data.name);
                            if (idx !== -1) virtuosoRef.current.scrollToIndex({ index: groupCounts.slice(0, idx).reduce((a, b) => a + b, 0), align: 'start' });
                        }}
                        type="button"
                        title={data.name}
                    >
                        <div><img src={groupImages[data.name]} alt={data.name} /></div>
                    </button>
                ))}
            </div>

            <div className={styles.emojiSection}>
                <GroupedVirtuoso
                    ref={virtuosoRef}
                    groupCounts={groupCounts}
                    groupContent={groupHeader}
                    itemContent={emojiRenderer}
                    style={{ height: '100%', width: '100%', overflowY: 'auto' }}
                    increaseViewportBy={100}
                />
            </div>
        </div>
    );
};

export default memo(EmojiPicker);
