import { useRef, useMemo, memo } from 'react';
import styles from '../styles/EmojiPicker.module.css';
import emojiData from 'unicode-emoji-json/data-by-group';
import DataSeparator from './DataSeparator';

const EmojiPicker = ({ onSelect }) => {
    const groupRefs = useRef({});

    const emojis = useMemo(() => Object.entries(emojiData), []);

    const handleScrollToGroup = (groupKey) => {
        const ref = groupRefs.current[groupKey];
        if (ref) {
            ref.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
    };

    const renderNavigation = () => (
        <div className={styles.nav}>
            {emojis.map(([groupKey, data]) => (
                <button
                    key={groupKey}
                    className={styles.navButton}
                    onClick={() => handleScrollToGroup(groupKey)}
                    type="button"
                    title={data.name}
                >
                    {data.emojis[0]?.emoji || '❓'}
                </button>
            ))}
        </div>
    );

    const renderEmojis = () => (
        emojis.map(([groupKey, data]) => (
            <div
                key={groupKey}
                ref={(el) => (groupRefs.current[groupKey] = el)}
                className={styles.groupContainer}
            >
                <DataSeparator styleText={
                    {
                        fontSize: '14px',
                        fontWeight: '400',
                        fontFamily: '"Ralaway" sans-serif',
                        color: '#ADADBD',
                        margin: '15px 0',
                        letterSpacing: '0%'
                    }
                }
                    data={data.name} />
                {data.emojis.map((emojiData) => (
                    <button
                        key={emojiData.emoji}
                        onClick={() => onSelect(emojiData.emoji)}
                        className={styles.emoji}
                        type="button"
                        aria-label={emojiData.name}
                        title={emojiData.name}
                    >
                        {emojiData.emoji}
                    </button>
                ))}
            </div>
        ))
    );

    return (
        <div className={styles.container}>
            {renderNavigation()}
            <div className={styles.emojiSection}>
                {renderEmojis()}
            </div>
        </div>
    );
};

export default memo(EmojiPicker);
